package edureka1;

public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10/3);
		System.out.println(10%3);
		System.out.println(10%-3);
		System.out.println(-10%-3);
		//System.out.println(10/0);
		System.out.println(10.0/0);
		System.out.println(-10.0/0);
		System.out.println(0.0/0);

	}

}
